package practice123;

public class Example2tringle {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a=1;
		for (int i = 0; i < 4; i++) {
			
			for (int j = 0; j < 4-i; j++) {
				
				System.out.print(a+" ");
				a++;
			}
			System.out.println("");
			
	}
		int b=9;
		for (int i2 = 1; i2 < 5; i2++) {
			
			for (int j2 = 1; j2 <=i2; j2++) {
				
				System.out.print(b+ " ");
				b--;
			}
			System.out.println("");
			
		}
	}
	

}
