package practice123;

public class Tringlenumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		int a= 1;
		for (int i = 0; i < 5; i++) {
			
			for (int j = 0; j < i; j++) {
				
				System.out.print(a+"  ");
				a++;
				
			}
			System.out.println("");
		}
	}

}
